package Topic1.Task2;

import java.math.BigDecimal;
import java.math.RoundingMode;

public class Task2JustForFun {
    public static BigDecimal taylor(BigDecimal x) {
        BigDecimal one = new BigDecimal("1.0");
        BigDecimal s = new BigDecimal("1.0");
        BigDecimal a = x;
        BigDecimal n = new BigDecimal("1.0");
        while (!s.add(a).equals(s)) {
            s = s.add(a);
            n = n.add(one);
            a = a.multiply(x).divide(n, 10_000, RoundingMode.CEILING);
        }
        return s;
    }

    public static void main(String[] args) {
        System.out.println(taylor(BigDecimal.valueOf(-25.0))); //  x = -25    result = 174.7128 in float
    }
/* x = -25
taylor = 1.-11
 */
}
